import { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { ReactComponent as EccLogo } from "../Resource/svg/EccLogo.svg";
import GlobalFont from "../Resource/font/fonts";
import {
  SignInContainer,
  SignInInputAreaContainer,
  SignInInputArea,
  AutoLogin,
  AutoLoginCotainer,
  LoginButton,
} from "./AuthenticationStyleComponent";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { initializeApp } from "firebase/app";
import { firebaseConfig } from "../App";
const EccText = styled.h3`
  font-family: "roboto";
  color: #3763ff;
  opacity: 0.4;
`;
const app = initializeApp(firebaseConfig);
// 파이어베이스 권한 초기화
const auth = getAuth();

// 파이어베이스를 활용한 회원인증(로그인)
const signIn = (email: any, password: any) => {
  signInWithEmailAndPassword(auth, email.value, password.value)
    .then((userCredential) => {
      console.log(userCredential);
    })
    .catch((error) => {
      const errorCode = error.code;
      alert(errorCode);
      const errorMessage = error.message;
    });
};

const SignIn = () => {
  const check = useRef<HTMLInputElement>(null);
  const [isChecked, setIschecked] = useState<boolean>(false);
  const email = useRef<HTMLInputElement>(null);
  const password = useRef<HTMLInputElement>(null);

  // useEffect(() => {
  //   alert(`${isChecked === true ? "자동로그인 적용" : "자동로그인 해제"}`);
  // }, [isChecked]);

  return (
    <SignInContainer>
      <EccLogo style={{ margin: "auto" }} />
      <GlobalFont />
      <EccText>EXPANDED CORE CURRICULUM</EccText>
      <SignInInputAreaContainer>
        <SignInInputArea ref={email} placeholder="이메일" type={"email"} />
        <SignInInputArea
          ref={password}
          placeholder="비밀번호"
          type={"password"}
        />
      </SignInInputAreaContainer>
      <AutoLoginCotainer>
        <AutoLogin
          ref={check}
          type={"checkbox"}
          onChange={() => {
            setIschecked((prev) => {
              return !prev;
            });
          }}
        />
        <label
          style={{ position: "absolute", left: "5rem", paddingTop: "0.4rem" }}
        >
          자동 로그인
        </label>
      </AutoLoginCotainer>
      <LoginButton
        onClick={() => {
          signIn(email.current, password.current);
        }}
      >
        로그인
      </LoginButton>
    </SignInContainer>
  );
};

export default SignIn;
